import img from './P1.jpg'

function Card(props) {
  return (

  	<div className='tc bg-light-blue dib br3 pa3 ma2 grow bw2 shadow-5' >
  		<img alt='Calligraphy' src={img} width="400" height="400"/>
 	    <div>
		    <h2>{props.id}</h2>
		    <p>{props.name}</p>
	    </div>
	</div>
  );
}

export default Card;
